package javapracticles;

public class IncrementDcrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int  k=10;
        		
		System.out.println(k++);//11
		System.out.println(++k);//12
		System.out.println(k--);//11
		System.out.println(--k);//10
		
	}

}
